    Serial.print(".");
  }
  Serial.println("");
  Serial.println("WiFi conectado.");
  Serial.println("Dirección IP: ");
  Serial.println(WiFi.localIP());

  // Iniciar el servidor
  server.begin();
}

void loop() {
  // Comprobar si hay un cliente
  WiFiClient client = server.available();
  if (client) {
    Serial.println("Nuevo Cliente.");
    String currentLine = "";

    // Mientras el cliente esté conectado
    while (client.connected()) {
      if (client.available()) {
        char c = client.read();
        Serial.write(c);
        if (c == '
') {
          if (currentLine.length() == 0) {
            // Enviar respuesta HTTP
            client.println("HTTP/1.1 200 OK");
            client.println("Content-type:text/html");
            client.println();

            // Contenido del servidor web
            client.println("<!DOCTYPE html><html>");
            client.println("<head><title>ESP32 Web Server</title></head>");
            client.println("<body><h1>Hola desde ESP32</h1></body>");
            client.println("</html>");

            // La respuesta HTTP se ha completado
            break;
          } else {
            currentLine = "";
          }
        } else if (c != '') {
          currentLine += c;
        }
      }
    }
    // Cerrar la conexión
    client.stop();
    Serial.println("Cliente Desconectado.");
  }
}

