// Crear una instancia del ADS1115
Adafruit_ADS1115 ads;

void setup(void) {
  Serial.begin(9600);
  Serial.println("Inicializando ADS1115...");

  // Inicializar el ADS1115
  ads.begin();
}

void loop(void) {
  // Leer valores de los cuatro canales
  int16_t adc0, adc1, adc2, adc3;

  adc0 = ads.readADC_SingleEnded(0);
  adc1 = ads.readADC_SingleEnded(1);
  adc2 = ads.readADC_SingleEnded(2);
  adc3 = ads.readADC_SingleEnded(3);

  // Convertir los valores a voltaje
  float volts0, volts1, volts2, volts3;

  volts0 = adc0 * 0.1875 / 1000;
  volts1 = adc1 * 0.1875 / 1000;
  volts2 = adc2 * 0.1875 / 1000;
  volts3 = adc3 * 0.1875 / 1000;

  // Imprimir los valores leídos en el monitor serie
  Serial.print("ADC0: "); Serial.print(adc0); Serial.print(" | Voltaje0: "); Serial.print(volts0); Serial.println(" V");
  Serial.print("ADC1: "); Serial.print(adc1); Serial.print(" | Voltaje1: "); Serial.print(volts1); Serial.println(" V");
  Serial.print("ADC2: "); Serial.print(adc2); Serial.print(" | Voltaje2: "); Serial.print(volts2); Serial.println(" V");
  Serial.print("ADC3: "); Serial.print(adc3); Serial.print(" | Voltaje3: "); Serial.print(volts3); Serial.println(" V");

