#include <Arduino.h>

// Definir los pines de selección del canal
const int s0 = 2;
const int s1 = 3;
const int s2 = 4;
const int s3 = 5;

// Definir el pin de habilitación (activo en bajo)
const int en = 6;

// Definir el pin de señal común
const int sig = A0;

void setup() {
  // Configurar los pines de selección y habilitación como salidas
  pinMode(s0, OUTPUT);
  pinMode(s1, OUTPUT);
  pinMode(s2, OUTPUT);
  pinMode(s3, OUTPUT);
  pinMode(en, OUTPUT);

  // Configurar el pin de señal como entrada
  pinMode(sig, INPUT);

  // Deshabilitar el multiplexor inicialmente
  digitalWrite(en, HIGH);

  // Iniciar la comunicación serie para el monitoreo
  Serial.begin(9600);
}

void loop() {
  // Habilitar el multiplexor
  digitalWrite(en, LOW);

  // Leer cada uno de los 16 canales
  for (int channel = 0; channel < 16; channel++) {
    // Seleccionar el canal configurando los pines de selección
    digitalWrite(s0, channel & 0x01);
    digitalWrite(s1, (channel >> 1) & 0x01);
    digitalWrite(s2, (channel >> 2) & 0x01);
    digitalWrite(s3, (channel >> 3) & 0x01);

    // Leer el valor analógico del canal seleccionado
    int value = analogRead(sig);

    // Imprimir el canal y el valor leído
    Serial.print("Canal ");
    Serial.print(channel);
    Serial.print(": ");
    Serial.println(value);

    // Esperar un poco antes de leer el siguiente canal
    delay(100);
  }

  // Deshabilitar el multiplexor para ahorrar energía
  digitalWrite(en, HIGH);

  // Esperar un segundo antes de la siguiente lectura
  delay(1000);
}

#include <Wire.h>
#include "Adafruit_TCS34725.h"

// Inicializa el sensor TCS34725
Adafruit_TCS34725 tcs = Adafruit_TCS34725(TCS34725_INTEGRATIONTIME_50MS, TCS34725_GAIN_4X);

void setup() {
  Serial.begin(9600);
  if (tcs.begin()) {
    Serial.println("TCS34725 sensor found.");
  } else {
    Serial.println("No TCS34725 sensor found ... check your wiring.");
    while (1); // Halt
  }

  // Opcional: Apagar el LED
  // tcs.setInterrupt(true);
}

void loop() {
  uint16_t r, g, b, c;

  // Leer valores de color
  tcs.getRawData(&r, &g, &b, &c);

  // Imprimir valores de color en el monitor serie
  Serial.print("Rojo: "); Serial.print(r, DEC);
  Serial.print(" Verde: "); Serial.print(g, DEC);
  Serial.print(" Azul: "); Serial.print(b, DEC);
  Serial.print(" Claro: "); Serial.println(c, DEC);

  // Convertir a valores RGB (con corrección de gamma)
  uint32_t sum = r + g + b;
  float red, green, blue;
  red = r; red /= sum;
  green = g; green /= sum;
  blue = b; blue /= sum;

  red *= 256; green *= 256; blue *= 256;

  Serial.print("RGB Values (Corrected): ");
  Serial.print((int)red); Serial.print(", ");
  Serial.print((int)green); Serial.print(", ");
  Serial.print((int)blue); Serial.println(" ");

  // Pausa para leer los valores
  delay(1000);
}

#include <OneWire.h>
