const int ledPin = 2; // El pin GPIO donde está conectado el LED
const int buttonPin = 4; // El pin GPIO donde está conectado el microinterruptor

void setup() {
  // Inicializar el pin del LED como salida
  pinMode(ledPin, OUTPUT);
  // Inicializar el pin del microinterruptor como entrada
  pinMode(buttonPin, INPUT_PULLUP);
}

void loop() {
  // Leer el estado del microinterruptor
  int buttonState = digitalRead(buttonPin);

  // Si el microinterruptor está presionado, encender el LED
  if (buttonState == LOW) {
    digitalWrite(ledPin, HIGH);
  } else {
    // Si el microinterruptor no está presionado, apagar el LED
    digitalWrite(ledPin, LOW);
  }
}

#include "DHT.h"

#define DHTPIN 4     // Pin donde se conecta el sensor DHT11
#define DHTTYPE DHT11   // Definir el tipo de sensor DHT11

DHT dht(DHTPIN, DHTTYPE);

void setup() {
  Serial.begin(115200);
  dht.begin();
}

void loop() {
  // Esperar entre lecturas
  delay(2000);

  // Leer la humedad
  float h = dht.readHumidity();
  // Leer la temperatura como Celsius
  float t = dht.readTemperature();

  // Comprobar si alguna lectura falló y salir de la función early
  if (isnan(h) || isnan(t)) {
    Serial.println("Fallo en la lectura del sensor DHT11!");
    return;
  }

  // Mostrar las lecturas en el monitor serie
  Serial.print("Humedad: ");
  Serial.print(h);
  Serial.print(" %	");
  Serial.print("Temperatura: ");
  Serial.print(t);
  Serial.println(" *C ");
}

