#include <HardwareSerial.h>

HardwareSerial MySerial(1);  // Utiliza UART1 del ESP32

void setup() {
  Serial.begin(115200);       // Inicializa la comunicación serial con el monitor serial
  MySerial.begin(9600, SERIAL_8N1, 16, 17);  // Configura UART1 con TX en GPIO16 y RX en GPIO17

  Serial.println("Iniciando comunicación con JDY-30...");
}

void loop() {
  if (MySerial.available()) {        // Si hay datos disponibles en el JDY-30
    String data = MySerial.readString();  // Lee los datos recibidos
    Serial.print("Recibido: ");      // Imprime los datos en el monitor serial
    Serial.println(data);
  }

  if (Serial.available()) {         // Si hay datos disponibles en el monitor serial
    String data = Serial.readString();  // Lee los datos recibidos
    MySerial.print(data);            // Envía los datos al JDY-30
  }
}

